import numpy as np

def calculate_sum_of_s(grid: np.ndarray) -> int:
    n, m = grid.shape
    sum_of_s = 0

    # Precompute the distances from each pole to all points in the grid
    distances = np.full((n, m, n, m), np.inf)

    for i in range(n):
        for j in range(m):
            if grid[i, j] == 1:
                for x in range(n):
                    for y in range(m):
                        distance_squared = (x - i) ** 2 + (y - j) ** 2
                        if distance_squared < distances[x, y, i, j]:
                            distances[x, y, i, j] = distance_squared
                        if distance_squared < distances[i, j, x, y]:
                            distances[i, j, x, y] = distance_squared

    # Calculate the sum of S(x, y) for each point in the grid
    for x in range(n):
        for y in range(m):
            min_distance_squared = np.min(distances[x, y])
            sum_of_s += min_distance_squared

    return sum_of_s

