from typing import List, Tuple

def calculate_max_steps(n: int, m: int, board: List[List[str]]) -> Tuple[int, int, int]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m

    def simulate(start_x: int, start_y: int) -> int:
        visited = [[False] * m for _ in range(n)]
        x, y = start_x, start_y
        steps = 0
        while is_valid(x, y) and not visited[x][y]:
            visited[x][y] = True
            direction = board[x][y]
            if direction == 'L':
                y -= 1
            elif direction == 'R':
                y += 1
            elif direction == 'D':
                x += 1
            elif direction == 'U':
                x -= 1
            steps += 1
        return steps

    max_steps = 0
    best_start = (0, 0, 0)

    for i in range(n):
        for j in range(m):
            steps = simulate(i, j)
            if steps > max_steps:
                max_steps = steps
                best_start = (i + 1, j + 1, steps)

    return best_start

