def func(queries: typing.List[str]) -> typing.List[int]:
    array = []
    replacement_map = {}

    for query in queries:
        parts = query.split()
        if parts[0] == '1':
            # Append the element x to the end of the array
            x = int(parts[1])
            array.append(x)
        elif parts[0] == '2':
            # Replace all occurrences of x with y in the array
            x = int(parts[1])
            y = int(parts[2])
            if x not in replacement_map:
                replacement_map[x] = y
            else:
                array = [y if elem == x else elem for elem in array]

    # Perform replacements based on the replacement_map
    for i, elem in enumerate(array):
        if elem in replacement_map:
            array[i] = replacement_map[elem]

    return array

