    def is_non_prime(num: int) -> bool:
        if num <= 1:
            return True
        if num <= 3:
            return False
        if num % 2 == 0 or num % 3 == 0:
            return True
        i = 5
        while i * i <= num:
            if num % i == 0 or num % (i + 2) == 0:
                return True
            i += 6
        return False

    max_steps = 0
    best_start = (0, 0, 0)

    for r in range(n):
        for c in range(m):
            directions = board[r][c]
            steps = 0
            visited = set()
            cr, cc = r, c

            while (cr, cc) not in visited:
                visited.add((cr, cc))
                direction = directions[steps % len(directions)]
                if direction == 'L':
                    cc -= 1
                elif direction == 'R':
                    cc += 1
                elif direction == 'D':
                    cr += 1
                elif direction == 'U':
                    cr -= 1

                if cr < 0 or cr >= n or cc < 0 or cc >= m:
                    break

                steps += 1

            if steps > max_steps:
                max_steps = steps
                best_start = (r + 1, c + 1, steps)

    return best_start

