            visited[x][y] = True
            direction = board[x][y]
            if direction == 'L':
                y -= 1
            elif direction == 'R':
                y += 1
            elif direction == 'D':
                x += 1
            elif direction == 'U':
                x -= 1
            steps += 1
        return steps

