def count_pairs_to_remove(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    k = total_sum / n
    count = 0
    element_count = {}

    # Count occurrences of each element
    for num in arr:
        element_count[num] = element_count.get(num, 0) + 1

    # Check pairs
    for i in range(n):
        for j in range(i + 1, n):
            if (total_sum - arr[i] - arr[j]) / (n - 2) == k:
                count += 1

    return count

