from typing import List

def calculate_sum_of_s(n: int, m: int, grid: List[List[int]]) -> int:
    def distance_squared(x1: int, y1: int, x2: int, y2: int) -> int:
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    # Precompute the minimum distance to the nearest pole for each point
    min_distance = [[0] * (m + 1) for _ in range(n + 1)]
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                min_distance[x][y] = 0
            else:
                min_distance[x][y] = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            dist = distance_squared(x, y, i, j)
                            if dist < min_distance[x][y]:
                                min_distance[x][y] = dist

    # Calculate the sum of S(x, y) using the precomputed distances
    sum_of_s = 0
    for x in range(n + 1):
        for y in range(m + 1):
            sum_of_s += min_distance[x][y]

    return sum_of_s

