from typing import List

def calculate_pairs_to_delete(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    original_mean = total_sum / n

    # Create a hash map to store the counts of the differences
    diff_count = {}

    for num in arr:
        diff = num - original_mean
        if diff in diff_count:
            diff_count[diff] += 1
        else:
            diff_count[diff] = 1

    pairs_to_delete = 0

    for i in range(n):
        for j in range(i + 1, n):
            # Calculate the difference when elements at positions i and j are deleted
            diff = (arr[i] + arr[j]) - original_mean
            if diff in diff_count:
                pairs_to_delete += diff_count[diff]

    return pairs_to_delete

