import sys

def process_queries(q: int) -> list:
    array = []
    element_count = {}

    for _ in range(q):
        query = input().split()
        if query[0] == '1':
            x = int(query[1])
            array.append(x)
            if x in element_count:
                element_count[x] += 1
            else:
                element_count[x] = 1
        elif query[0] == '2':
            x = int(query[1])
            y = int(query[2])
            if x in element_count:
                for _ in range(element_count[x]):
                    array.remove(x)
                if y in element_count:
                    element_count[y] += element_count[x]
                else:
                    element_count[y] = element_count[x]
                array.extend([y] * element_count[x])
                del element_count[x]

    return array

