from typing import List, Tuple
import sys

# Assuming a Tree is a list of lists where each sublist represents a node and its children
Tree = List[List[int]]

# Initialize the tree with n rooms and n-1 tunnels
def initialize_tree(n: int) -> Tree:
    return [[] for _ in range(n + 1)]

# Update the illusion rate of a room
def update_illusion_rate(room_id: int, new_rate: int, tree: Tree, illusion_rates: List[int]) -> None:
    illusion_rates[room_id] = new_rate

# Calculate the minimum energy required to travel between two rooms
def calculate_minimum_energy(start_room: int, end_room: int, tree: Tree, illusion_rates: List[int]) -> int:
    # This is a placeholder for the actual graph traversal algorithm
    # We will use a simple recursive approach to calculate the energy
    def dfs(node: int, target: int, energy: int, visited: List[bool]) -> int:
        if node == target:
            return energy
        visited[node] = True
        min_energy = sys.maxsize
        for child in tree[node]:
            if not visited[child]:
                child_energy = dfs(child, target, max(abs(illusion_rates[node] + illusion_rates[child]), abs(illusion_rates[node] - illusion_rates[child])), visited)
                min_energy = min(min_energy, child_energy)
        visited[node] = False
        return min_energy

    visited = [False] * (len(tree))
    return dfs(start_room, end_room, 0, visited)

# Main function to handle input and queries
def main():
    n, q = map(int, sys.stdin.readline().split())
    illusion_rates = list(map(int, sys.stdin.readline().split()))
    tree = initialize_tree(n)

    for _ in range(n - 1):
        s, t = map(int, sys.stdin.readline().split())
        tree[s].append(t)
        tree[t].append(s)

    for _ in range(q):
        query = list(map(int, sys.stdin.readline().split()))
        if query[0] == 1:
            update_illusion_rate(query[1], query[2], tree, illusion_rates)
        elif query[0] == 2:
            result = calculate_minimum_energy(query[1], query[2], tree, illusion_rates)
            print(result)

if __name__ == "__main__":
    main()

