from typing import List

def find_max_f(sequence: List[int]) -> int:
    n = len(sequence)
    max_f = 0

    for i in range(n):
        if sequence[i] == i + 1:
            max_f += 1

    # Additional logic to consider removing elements for maximizing f(a)
    for i in range(n):
        for j in range(i + 1, n):
            temp_sequence = sequence[:i] + sequence[i+1:j] + sequence[j+1:]
            temp_f = 0
            for k in range(len(temp_sequence)):
                if temp_sequence[k] == k + 1:
                    temp_f += 1
            max_f = max(max_f, temp_f)

    return max_f

