from typing import List

def calculate_sum_of_distances(n: int, m: int, grid: List[List[int]]) -> int:
    # Function to calculate the Euclidean distance squared between two points
    def distance_squared(x1, y1, x2, y2):
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    # Precompute the 2D prefix sum for distances
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1] + distance_squared(i, j, grid[i][j] - 1, grid[i][j] - 1)

    # Function to query the sum of distances for a given rectangle
    def query(x1, y1, x2, y2):
        return prefix_sum[x2][y2] - prefix_sum[x1 - 1][y2] - prefix_sum[x2][y1 - 1] + prefix_sum[x1 - 1][y1 - 1]

    # Initialize the sum of distances
    sum_of_distances = 0

    # Iterate over each point in the grid
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                # If the point is a pole, S(x, y) is 0
                sum_of_distances += 0
            else:
                # If the point is not a pole, find the nearest pole and calculate S(x, y)
                min_distance_squared = float('inf')
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            dist_squared = distance_squared(x, y, i, j)
                            if dist_squared < min_distance_squared:
                                min_distance_squared = dist_squared
                sum_of_distances += min_distance_squared

    return sum_of_distances

