def identify_non_primes(limit: int) -> List[int]:
    def is_prime(n: int) -> bool:
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    non_primes = []
    for num in range(2, limit + 1):
        if not is_prime(num):
            non_primes.append(num)
    return non_primes

