def calculate_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m

    def bfs(start_x: int, start_y: int) -> int:
        visited = [[False] * m for _ in range(n)]
        queue = deque([(start_x, start_y, 0)])  # (x, y, steps)
        visited[start_x][start_y] = True
        max_steps = 0

        while queue:
            x, y, steps = queue.popleft()
            max_steps = max(max_steps, steps)
            direction = board[x][y]

            if direction == 'L':
                next_y = y - 1
                if is_valid(x, next_y) and not visited[x][next_y]:
                    visited[x][next_y] = True
                    queue.append((x, next_y, steps + 1))
            elif direction == 'R':
                next_y = y + 1
                if is_valid(x, next_y) and not visited[x][next_y]:
                    visited[x][next_y] = True
                    queue.append((x, next_y, steps + 1))
            elif direction == 'D':
                next_x = x + 1
                if is_valid(next_x, y) and not visited[next_x][y]:
                    visited[next_x][y] = True
                    queue.append((next_x, y, steps + 1))
            elif direction == 'U':
                next_x = x - 1
                if is_valid(next_x, y) and not visited[next_x][y]:
                    visited[next_x][y] = True
                    queue.append((next_x, y, steps + 1))

        return max_steps

    max_steps_overall = 0
    best_start = (0, 0, 0)

    for start_x in range(n):
        for start_y in range(m):
            current_steps = bfs(start_x, start_y)
            if current_steps > max_steps_overall:
                max_steps_overall = current_steps
                best_start = (start_x + 1, start_y + 1, current_steps)

    return best_start

