def calculate_sum_of_distances(n: int, m: int, grid: List[List[int]]) -> int:
    def distance_squared(x1, y1, x2, y2):
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    sum_of_distances = 0

    # Precompute the minimum distance to a pole for each point
    min_distance = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    # Initialize the min_distance array with the correct values
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                min_distance[x][y] = 0
            else:
                if x > 0:
                    min_distance[x][y] = min(min_distance[x][y], min_distance[x - 1][y] + 1)
                if y > 0:
                    min_distance[x][y] = min(min_distance[x][y], min_distance[x][y - 1] + 1)
                if x < n:
                    min_distance[x][y] = min(min_distance[x][y], min_distance[x + 1][y] + 1)
                if y < m:
                    min_distance[x][y] = min(min_distance[x][y], min_distance[x][y + 1] + 1)

    # Calculate the sum of all S(x, y)
    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 0:
                sum_of_distances += min_distance[x][y] ** 2

    return sum_of_distances

