from typing import List

def calculate_sum_of_distances(n: int, m: int, grid: List[List[int]]) -> int:
    # Function to calculate the sum of distances using a 2D prefix sum technique

    # Initialize the prefix sum array with zeros
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Fill the prefix sum array
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = grid[i][j] + prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1]

    # Function to calculate the sum of distances for a given rectangle
    def sum_of_distances(x1, y1, x2, y2):
        total_poles = prefix_sum[x2][y2] - prefix_sum[x1 - 1][y2] - prefix_sum[x2][y1 - 1] + prefix_sum[x1 - 1][y1 - 1]
        if total_poles == 0:
            return 0

        sum_dist = 0
        for x in range(x1, x2 + 1):
            for y in range(y1, y2 + 1):
                if grid[x][y] == 1:
                    sum_dist += (x - (x1 + (x2 - x1) // 2)) ** 2 + (y - (y1 + (y2 - y1) // 2)) ** 2

        return sum_dist

    # Calculate the sum of all S(x, y) for each 0 ≤ x ≤ n and 0 ≤ y ≤ m
    result = 0
    for x in range(1, n + 1):
        for y in range(1, m + 1):
            result += sum_of_distances(x, y, x, y)

    return result

