from typing import List, Tuple
import heapq

# Assuming a simple tree structure using adjacency list
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List['TreeNode'] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

class Tree:
    def __init__(self, n: int, room_illusion_rates: List[int]):
        self.nodes = {i + 1: TreeNode(i + 1, room_illusion_rates[i]) for i in range(n)}
        self.root = self.nodes[1]  # Assuming the first room is the root

    def build_tree(self, edges: List[Tuple[int, int]]):
        for s, t in edges:
            self.nodes[s].add_child(self.nodes[t])
            self.nodes[t].add_child(self.nodes[s])

    def update_illusion_rate(self, room_id: int, new_rate: int):
        node = self._find_node(room_id)
        if node:
            node.illusion_rate = new_rate

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        start_node = self._find_node(start_room)
        end_node = self._find_node(end_room)
        if not start_node or not end_node:
            return 0  # or raise an error, depending on your error handling strategy

        # Using a priority queue (heap) for Dijkstra's algorithm
        priority_queue = [(0, start_node)]
        distances = {node: float('inf') for node in self.nodes.values()}
        distances[start_node] = 0
        while priority_queue:
            current_distance, current_node = heapq.heappop(priority_queue)
            if current_node.room_id == end_node.room_id:
                return current_distance + abs(start_node.illusion_rate + end_node.illusion_rate)
            if current_distance > distances[current_node]:
                continue
            for child in current_node.children:
                energy = abs(current_node.illusion_rate + child.illusion_rate)
                distance = current_distance + energy
                if distance < distances[child]:
                    distances[child] = distance
                    heapq.heappush(priority_queue, (distance, child))
        return 0  # or raise an error, depending on your error handling strategy

    def _find_node(self, room_id: int) -> TreeNode:
        return self.nodes.get(room_id)

# Interface functions
def update_illusion_rate(room_id: int, new_rate: int) -> None:
    global tree
    tree.update_illusion_rate(room_id, new_rate)

def calculate_minimum_energy(start_room: int, end_room: int) -> int:
    global tree
    return tree.calculate_minimum_energy(start_room, end_room)

