from collections import deque

def find_max_steps(board, n, m):
    def bfs(start_row, start_col):
        visited = [[False] * m for _ in range(n)]
        queue = deque([(start_row, start_col, 0)])  # (row, col, steps)
        visited[start_row][start_col] = True
        max_steps = 0

        while queue:
            row, col, steps = queue.popleft()
            max_steps = max(max_steps, steps)

            if board[row][col] == 'L':
                if col - 1 >= 0 and not visited[row][col - 1]:
                    visited[row][col - 1] = True
                    queue.append((row, col - 1, steps + 1))
            elif board[row][col] == 'R':
                if col + 1 < m and not visited[row][col + 1]:
                    visited[row][col + 1] = True
                    queue.append((row, col + 1, steps + 1))
            elif board[row][col] == 'D':
                if row + 1 < n and not visited[row + 1][col]:
                    visited[row + 1][col] = True
                    queue.append((row + 1, col, steps + 1))
            elif board[row][col] == 'U':
                if row - 1 >= 0 and not visited[row - 1][col]:
                    visited[row - 1][col] = True
                    queue.append((row - 1, col, steps + 1))

        return max_steps

    max_steps = 0
    best_start = (1, 1)

    for row in range(n):
        for col in range(m):
            current_steps = bfs(row, col)
            if current_steps > max_steps:
                max_steps = current_steps
                best_start = (row + 1, col + 1)

    return best_start[0], best_start[1], max_steps

