from typing import List, Tuple

def calculate_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def move_robot(start_r: int, start_c: int) -> int:
        visited = [[False] * m for _ in range(n)]
        steps = 0
        r, c = start_r, start_c

        while True:
            if visited[r][c]:
                return -1  # Robot breaks if it revisits a cell
            visited[r][c] = True
            direction = board[r][c]
            if direction == 'L':
                c -= 1
            elif direction == 'R':
                c += 1
            elif direction == 'D':
                r += 1
            elif direction == 'U':
                r -= 1

            if r < 0 or r >= n or c < 0 or c >= m:
                return steps  # Robot falls off the board

            steps += 1

    max_steps = -1
    best_position = (0, 0)

    for r in range(n):
        for c in range(m):
            steps = move_robot(r, c)
            if steps > max_steps:
                max_steps = steps
                best_position = (r + 1, c + 1)  # Convert to 1-based index

    return best_position + (max_steps,)

