import sys

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    max_dist = n + m

    # Precompute the minimum distance to the nearest pole for each point
    dist_grid = [[max_dist] * (m + 1) for _ in range(n + 1)]

    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                dist_grid[i][j] = 0
            elif i > 0:
                dist_grid[i][j] = min(dist_grid[i][j], dist_grid[i - 1][j] + 1)
            if j > 0:
                dist_grid[i][j] = min(dist_grid[i][j], dist_grid[i][j - 1] + 1)

    for i in range(n, -1, -1):
        for j in range(m, -1, -1):
            if i < n:
                dist_grid[i][j] = min(dist_grid[i][j], dist_grid[i + 1][j] + 1)
            if j < m:
                dist_grid[i][j] = min(dist_grid[i][j], dist_grid[i][j + 1] + 1)

    # Calculate the sum of all S(x, y)
    total_sum = 0
    for i in range(n + 1):
        for j in range(m + 1):
            total_sum += dist_grid[i][j] ** 2

    return total_sum

