def find_max_f(sequence: List[int]) -> int:
    n = len(sequence)
    max_f = 0

    # Count initial f(a)
    for i in range(n):
        if sequence[i] == i + 1:
            max_f += 1

    # Check the impact of removing each element
    for i in range(n):
        if sequence[i] == i + 1:
            continue  # Skip if a_i == i, as removing it won't increase f(a)
        current_f = 0
        for j in range(n):
            if j == i:
                continue  # Skip the current index
            if sequence[j] == j + 1:
                current_f += 1
        max_f = max(max_f, current_f)

    return max_f

