from typing import List
from collections import deque

def process_queries(q: int) -> List[int]:
    result_list = deque()

    for _ in range(q):
        query = input().split()
        query_type = int(query[0])

        if query_type == 1:
            x = int(query[1])
            result_list.append(x)
        elif query_type == 2:
            x = int(query[1])
            y = int(query[2])
            # Replace all occurrences of x with y in the list
            for i in range(len(result_list)):
                if result_list[i] == x:
                    result_list[i] = y

    return list(result_list)

# Read the number of queries
q = int(input())
result = process_queries(q)
print(' '.join(map(str, result)))
