def find_bipartite_array(n: int, p: List[int]) -> Optional[List[int]]:
    # Create a list to store the bipartite array
    a = [0] * n

    # Create a list to store the color of each vertex
    color = [-1] * n

    # Create a stack for DFS
    stack = []

    # Start DFS from the first vertex
    stack.append(0)
    color[0] = 0

    while stack:
        v = stack.pop()
        for u in range(v + 1, n):
            if p[v] > p[u]:
                if color[u] == -1:
                    color[u] = 1 - color[v]
                    stack.append(u)
                elif color[u] == color[v]:
                    return None

    # Construct the bipartite array
    for i in range(n):
        if color[i] == 0:
            a[i] = p[i]
        else:
            a[i] = -p[i]

    return a

