def find_minimum_operations(n: int, m: int, grid: List[List[int]]) -> int:
    def is_portal(x: int, y: int, a: int, b: int) -> bool:
        # Check the borders of the sub-rectangle
        for i in range(x, x + a):
            if grid[i][y] == 1 or grid[i][y + b - 1] == 1:
                return False
        for j in range(y, y + b):
            if grid[x][j] == 1 or grid[x + a - 1][j] == 1:
                return False
        # Check the inner part of the sub-rectangle
        for i in range(x + 1, x + a - 1):
            for j in range(y + 1, y + b - 1):
                if grid[i][j] == 1:
                    return False
        return True

    def count_operations(x: int, y: int, a: int, b: int) -> int:
        operations = 0
        for i in range(x, x + a):
            for j in range(y, y + b):
                if grid[i][j] == 1:
                    operations += 1
        return operations

    min_operations = float('inf')
    for x in range(n - 4):
        for y in range(m - 3):
            for a in range(5, n - x + 1):
                for b in range(4, m - y + 1):
                    if is_portal(x, y, a, b):
                        operations = count_operations(x, y, a, b)
                        min_operations = min(min_operations, operations)
    return min_operations if min_operations != float('inf') else -1

