def min_operations(n, start, end):
    # Initialize a DP table where dp[i][j] represents the minimum operations to transform
    # the first i characters of start to the first j characters of end.
    dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]

    # Base case: no operations needed to transform an empty string to an empty string.
    dp[0][0] = 0

    # Fill the DP table
    for i in range(n + 1):
        for j in range(n + 1):
            if i > 0:
                # Check if we can remove a Lego piece from start
                if start[i - 1] == '1' and end[j - 1] == '1':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + 1)
                else:
                    dp[i][j] = min(dp[i][j], dp[i - 1][j])
            if j > 0:
                # Check if we can add a Lego piece to start
                if start[i - 1] == '?' and end[j - 1] == '?':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + 1)
                elif start[i - 1] == '?' and end[j - 1] == '1':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + 1)
                elif start[i - 1] == '1' and end[j - 1] == '?':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + 1)
                else:
                    dp[i][j] = min(dp[i][j], dp[i - 1][j])

    return dp[n][n] if dp[n][n] != float('inf') else 0

