        for i in range(n):
            for j in range(m):
                if answers[i][j] == '1':
                    points[i] += permutation[j]
        return points

    # Helper function to calculate the surprise value
    def calculate_surprise_value(points: List[int]) -> int:
        return sum(abs(x - r) for x, r in zip(expected_points, points))

    # Generate all permutations of the questions
    from itertools import permutations

    max_surprise_value = 0
    best_permutation = []

    # Try all permutations of the questions
    for p in permutations(range(1, m + 1)):
        points = calculate_points(list(p))
        surprise_value = calculate_surprise_value(points)
        if surprise_value > max_surprise_value:
            max_surprise_value = surprise_value
            best_permutation = p

    return list(best_permutation)

