    for restriction in restrictions:
        u, v, w = restriction
        if (a == u and b == v) or (a == v and b == u):
            # Check if this edge violates the restriction
            if not ((b in tree[a] and w not in tree[a]) or (a in tree[b] and w not in tree[b])):
                return False
    return True

def construct_tree(n: int, restrictions: List[Tuple[int, int, int]]) -> List[Tuple[int, int]]:
    edges = []
    tree = [[] for _ in range(n + 1)]

    # Try to add edges one by one
    for i in range(2, n + 1):
        for j in range(1, i):
            if is_valid_edge((i, j), restrictions, tree):
                tree[i].append(j)
                tree[j].append(i)
                edges.append((i, j))
                break

    return edges

