def calculate_mex_correct_subsequences(n: int, arr: List[int]) -> int:
    from collections import Counter

    # Function to calculate MEX of a list of numbers
    def calculate_mex(nums: List[int]) -> int:
        counter = Counter(nums)
        for i in range(len(nums) + 1):
            if counter[i] == 0:
                return i
        return len(nums)

    # Function to count MEX-correct subsequences
    def count_mex_correct_subsequences(arr: List[int]) -> int:
        dp = [0] * (n + 1)
        dp[0] = 1  # There's always one empty subsequence

        for num in arr:
            new_dp = dp.copy()
            for i in range(n + 1):
                if abs(i - num) <= 1:
                    new_dp[num] = (new_dp[num] + dp[i]) % MOD
            dp = new_dp

        return (sum(dp) - 1) % MOD  # Subtract 1 for the empty subsequence

    return count_mex_correct_subsequences(arr)

