def sort_array(n: int, arr: list) -> tuple:
    shifts = []
    sorted_arr = sorted(arr)

    def apply_shift(l, r, d):
        segment = arr[l-1:r]
        shifted_segment = segment[d:] + segment[:d]
        arr[l-1:r] = shifted_segment
        shifts.append(f"{l} {r} {d}")

    # Check if the array is already sorted
    if arr == sorted_arr:
        return (0, [])

    # Optimized sorting using cyclic shifts
    for i in range(n):
        for j in range(i+1, n):
            for d in range(1, j - i + 1):
                temp_arr = arr[:]
                apply_shift(i+1, j+1, d)
                if temp_arr == sorted_arr:
                    return (len(shifts), shifts)
                else:
                    # Backtrack the shift
                    arr = temp_arr[:]
                    shifts.pop()

    return (len(shifts), shifts)

