    for i in range(len(s) - 1):
        if s[i:i+2] == 'ab' and ab_count > ba_count:
            return s[:i] + 'b' + s[i+1:]
        elif s[i:i+2] == 'ba' and ab_count < ba_count:
            return s[:i] + 'a' + s[i+1:]

    # If no direct change can balance the counts, change the last character
    if ab_count > ba_count:
        return s[:-1] + 'b'
    else:
        return s[:-1] + 'a'

