def func(n: int, cells: List[Tuple[int, int, int]]) -> int:
    # Group cells by color
    color_groups = defaultdict(list)
    for cell in cells:
        x, y, c = cell
        color_groups[c].append((x, y))

    # Function to check if two rectangles intersect
    def do_rectangles_intersect(rect1, rect2):
        (x1_min, x1_max, y1_min, y1_max), (x2_min, x2_max, y2_min, y2_max) = rect1, rect2
        return not (x1_max < x2_min or x2_max < x1_min or y1_max < y2_min or y2_max < y1_min)

    # Find the maximum number of cells that can be left for each color
    max_cells = 0
    for color in range(1, 4):
        if len(color_groups[color]) > n // 3:
            max_cells = max(max_cells, n // 3)
        else:
            max_cells = max(max_cells, len(color_groups[color]))

    # Use a greedy approach to find the largest set of cells that can form valid rectangles
    from itertools import combinations

    def can_form_rectangles(selected_cells):
        rectangles = [[float('inf'), -float('inf'), float('inf'), -float('inf')] for _ in range(4)]
        for idx in selected_cells:
            x, y, c = cells[idx]
            rectangles[c] = [min(rectangles[c][0], x), max(rectangles[c][1], x), min(rectangles[c][2], y), max(rectangles[c][3], y)]
            for other_color in range(1, 4):
                if other_color != c and do_rectangles_intersect(rectangles[c], rectangles[other_color]):
                    return False
        return True

    # Try to find the largest set of cells that can form valid rectangles
    for size in range(max_cells, 0, -1):
        for selected_cells in combinations(range(n), size * 3):
            if can_form_rectangles(selected_cells):
                return size * 3

    return max_cells * 3

