def count_perfect_sequences(n: int, M: int) -> int:
    # Function to calculate the modular exponentiation
    def mod_exp(base, exp, mod):
        result = 1
        base = base % mod
        while exp > 0:
            if exp % 2 == 1:
                result = (result * base) % mod
            exp = exp >> 1
            base = (base * base) % mod
        return result

    # Function to calculate the modular inverse
    def mod_inv(a, mod):
        return mod_exp(a, mod - 2, mod)

    # Precompute factorials and inverse factorials
    max_fact = n + 1
    fact = [1] * (max_fact)
    inv_fact = [1] * (max_fact)
    for i in range(2, max_fact):
        fact[i] = (fact[i - 1] * i) % M
    inv_fact[max_fact - 1] = mod_inv(fact[max_fact - 1], M)
    for i in range(max_fact - 2, 0, -1):
        inv_fact[i] = (inv_fact[i + 1] * (i + 1)) % M

    # Function to calculate nCr % M
    def nCr(n, r, mod):
        if r > n:
            return 0
        return (fact[n] * inv_fact[r] * inv_fact[n - r]) % mod

    # Main logic to count perfect sequences
    result = 0
    for i in range(1, n + 2):
        for j in range(1, n + 2):
            if i * j >= i + j:
                # Calculate the number of ways to choose the remaining elements
                remaining = n - 2
                if i == j:
                    remaining += 1
                result = (result + nCr(remaining, i - 1, M) * nCr(remaining, j - 1, M)) % M

    return result

