    for i in range(2, int(math.sqrt(g)) + 1):
        if g % i == 0:
            if is_beautiful_coloring(i):
                suitable_d.add(i)
            if is_beautiful_coloring(g // i):
                suitable_d.add(g // i)
    if suitable_d:
        return min(suitable_d)
    return 0

