def reconstruct_sequence(n: int, b: List[int]) -> Optional[List[int]]:
    a = [0] * n
    for i in range(n):
        # Initialize the minimum and maximum possible values for a[i]
        min_ai = 1
        max_ai = 10**9

        # Binary search to find the correct value of a[i]
        while min_ai <= max_ai:
            mid_ai = (min_ai + max_ai) // 2
            valid = True
            for k in range(n):
                if mid_ai * (k + 2) > b[(i + k) % n]:
                    valid = False
                    break
            if valid:
                a[i] = mid_ai
                break
            else:
                max_ai = mid_ai - 1

        # If no valid a[i] is found, return None
        if min_ai > max_ai:
            return None

    return a

