def func(grid: List[List[str]], n: int, m: int) -> List[List[str]]:
    # Find the position of the lab
    lab_pos = None
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'L':
                lab_pos = (i, j)
                break
        if lab_pos is not None:
            break

    # Directions: right, down, left, up
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]

    # Function to check if a cell can be forced to reach the lab
    def can_force_to_lab(x, y):
        visited = [[False] * m for _ in range(n)]
        queue = [(x, y)]
        visited[x][y] = True

        while queue:
            cx, cy = queue.pop(0)
            if (cx, cy) == lab_pos:
                return True

            for dx, dy in directions:
                nx, ny = cx + dx, cy + dy
                if 0 <= nx < n and 0 <= ny < m and not visited[nx][ny]:
                    if grid[nx][ny] == '.':
                        # Check if there is a direction to move to the lab
                        for ddx, ddy in directions:
                            if ddx != -dx and ddy != -dy:
                                nnx, nny = nx + ddx, ny + ddy
                                if 0 <= nnx < n and 0 <= nny < m and grid[nnx][nny] != '#' and (nnx, nny) != lab_pos:
                                    queue.append((nx, ny))
                                    visited[nx][ny] = True
                                    break
                        else:
                            queue.append((nx, ny))
                            visited[nx][ny] = True
                    elif grid[nx][ny] == 'L':
                        queue.append((nx, ny))
                        visited[nx][ny] = True

        return False

    # Mark the cells that the robot can be forced to reach the lab from
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.' and can_force_to_lab(i, j):
                grid[i][j] = '+'

    return grid

