def find_largest_composite_subset(arr: List[int]) -> Tuple[int, List[int]]:
    n = len(arr)
    max_subset_size = 0
    best_subset = []

    ## Generate all possible subsets and check their sums
    for mask in range(1, 1 << n):
        subset_sum = 0
        subset_indices = []
        for i in range(n):
            if mask & (1 << i):
                subset_sum += arr[i]
                subset_indices.append(i + 1)  ## 1-indexed

        if is_composite(subset_sum):
            if len(subset_indices) > max_subset_size:
                max_subset_size = len(subset_indices)
