def is_determinable(grid: List[List[str]], x1: int, x2: int) -> bool:
    n = len(grid)
    m = len(grid[0])

    # Create a prefix sum array to quickly check if a subarray is all empty
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1] + (1 if grid[i - 1][j - 1] == 'X' else 0)

    def sum_region(r1, c1, r2, c2):
        return prefix_sum[r2 + 1][c2 + 1] - prefix_sum[r1][c2 + 1] - prefix_sum[r2 + 1][c1] + prefix_sum[r1][c1]

    # Check if a subgrid is determinable
    for col1 in range(x1, x2 + 1):
        for col2 in range(col1, x2 + 1):
            all_empty = True
            for row in range(n):
                if sum_region(row, col1, row, col2) > 0:
                    all_empty = False
                    break
            if all_empty:
                return True
    return False

