def is_portal(grid: List[List[int]], x: int, y: int, a: int, b: int) -> bool:
    # Check if the sub-rectangle starting at (x, y) with size a x b can be a portal
    for i in range(x, x + a):
        if grid[i][y] == 1 or grid[i][y + b - 1] == 1:
            return False
    for j in range(y, y + b):
        if grid[x][j] == 1 or grid[x + a - 1][j] == 1:
            return False
    return True

def find_minimum_operations(grid: List[List[int]], a: int, b: int) -> int:
    n = len(grid)
    m = len(grid[0])
    operations = float('inf')

    # Check all possible sub-rectangles of size a x b
    for i in range(n - a + 1):
        for j in range(m - b + 1):
            if is_portal(grid, i, j, a, b):
                current_operations = 0
                # Count the number of operations needed to make this sub-rectangle a portal
                for x in range(i, i + a):
                    for y in range(j, j + b):
                        if grid[x][y] == 1:
                            current_operations += 1
                operations = min(operations, current_operations)

    return operations * 2  # Each operation can either turn an obsidian block to empty or empty to obsidian

