def find_valid_edge_pairs(N: int, M: int, edges: List[Tuple[int, int]]) -> int:
    # Initialize the graph as an adjacency list
    graph = {i: [] for i in range(1, N + 1)}
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    # Function to find the connected components using DFS
    def dfs(node, visited, component):
        stack = [node]
        while stack:
            current = stack.pop()
            if current not in visited:
                visited.add(current)
                component.append(current)
                for neighbor in graph[current]:
                    if neighbor not in visited:
                        stack.append(neighbor)

    # Find all connected components
    visited = set()
    components = []
    for node in range(1, N + 1):
        if node not in visited:
            component = []
            dfs(node, visited, component)
            components.append(component)

    # Function to check if a graph is a cactus
    def is_cactus(edges_to_consider):
        graph_cactus = {i: [] for i in range(1, N + 1)}
        for u, v in edges_to_consider:
            graph_cactus[u].append(v)
            graph_cactus[v].append(u)

        # Check for cycles using DFS
        visited_cactus = [False] * (N + 1)
        stack = [(node, -1) for node in range(1, N + 1)]
        while stack:
            current, parent = stack.pop()
            if not visited_cactus[current]:
                visited_cactus[current] = True
                for neighbor in graph_cactus[current]:
                    if neighbor != parent:
                        stack.append((neighbor, current))
                    elif visited_cactus[neighbor]:
                        return False
        return True

    # Count valid edge pairs
    count = 0
    for L in range(M):
        for R in range(L, M):
            if is_cactus(edges[L:R+1]):
                count += 1
    return count

