def find_best_starting_position(n: int, m: int, commands: str) -> Tuple[int, int]:
    # Initialize the best starting position and the maximum number of commands
    best_start = (1, 1)
    max_commands = 0

    # Create a dictionary to store the furthest position for each starting cell
    furthest_positions = {}

    # Iterate over all possible starting positions
    for start_row in range(1, n + 1):
        for start_col in range(1, m + 1):
            # Initialize the current position and the number of commands executed
            current_row, current_col = start_row, start_col
            commands_executed = 0

            # Execute the commands starting from the current position
            for command in commands:
                if command == 'L' and current_col > 1:
                    current_col -= 1
                elif command == 'R' and current_col < m:
                    current_col += 1
                elif command == 'D' and current_row < n:
                    current_row += 1
                elif command == 'U' and current_row > 1:
                    current_row -= 1

                # Increment the number of commands executed
                commands_executed += 1

            # Store the furthest position for the starting cell
            if (current_row, current_col) not in furthest_positions or commands_executed > furthest_positions[(current_row, current_col)]:
                furthest_positions[(current_row, current_col)] = commands_executed

    # Find the starting position that allows the most commands
    for (row, col), commands_executed in furthest_positions.items():
        if commands_executed > max_commands:
            max_commands = commands_executed
            best_start = (row, col)

    return best_start

