import sys

def balance_ab_ba(s: str) -> str:
    ab_count = s.count('ab')
    ba_count = s.count('ba')

    # If the counts are already equal, return the original string
    if ab_count == ba_count:
        return s

    # Find the first position to change to balance the counts
    for i in range(len(s) - 1):
        if s[i:i+2] == 'ab':
            return s[:i] + 'b' + s[i+1:]
        elif s[i:i+2] == 'ba':
            return s[:i] + 'a' + s[i+1:]

    # If no direct change can balance the counts, change the last character
    return s[:-1] + ('b' if ab_count > ba_count else 'a')

