def find_number_of_sequences(n: int, k: int, x: int) -> int:
    # Since x is limited to the range of 2^min(20, k), we can precompute the results for x values
    if x == 0:
        # If x is 0, we need to exclude all sequences that have a subsequence with XOR equal to 0
        # This is a combinatorial problem that can be solved using dynamic programming
        max_subsequence_length = min(20, k)
        dp = [[0] * (max_subsequence_length + 1) for _ in range(n + 1)]
        dp[0][0] = 1

        for i in range(1, n + 1):
            for j in range(max_subsequence_length + 1):
                dp[i][j] = dp[i - 1][j] * (2**k - j) % MOD
                if j > 0:
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - 1] * (j + 1)) % MOD

        return dp[n][max_subsequence_length]
    else:
        # If x is not 0, we can use the same dynamic programming approach as before
        max_subsequence_length = min(20, k)
        dp = [[0] * (max_subsequence_length + 1) for _ in range(n + 1)]
        dp[0][0] = 1

        for i in range(1, n + 1):
            for j in range(max_subsequence_length + 1):
                for y in range(2**k):
                    if j > 0 and (x ^ y) == 0:
                        continue
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - 1]) % MOD

        return dp[n][max_subsequence_length]

