    def can_draw_tick(i, j, d):
        if i - d < 0 or i + d >= n or j - d < 0 or j + d >= m:
            return False
        for h in range(d + 1):
            if field[i - h][j] != '*' or field[i + h][j] != '*' or field[i][j - h] != '*' or field[i][j + h] != '*':
                return False
        return True

    # Function to mark the cells of a tick at (i, j) with size d
    def mark_tick(i, j, d):
        for h in range(d + 1):
            painted[i - h][j] = True
            painted[i + h][j] = True
            painted[i][j - h] = True
            painted[i][j + h] = True

    # Check each cell in the field
