def is_determinable(grid: List[List[str]], x1: int, x2: int) -> bool:
    n = len(grid)
    m = len(grid[0])

    # Create a prefix sum array to quickly check if a subarray is all empty
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1]
            if grid[i - 1][j - 1] == 'X':
                prefix_sum[i][j] += 1

    def query_sum(r1, c1, r2, c2):
        return prefix_sum[r2][c2] - prefix_sum[r1 - 1][c2] - prefix_sum[r2][c1 - 1] + prefix_sum[r1 - 1][c1 - 1]

    def is_exitable(start_col):
        for row in range(n):
            if grid[row][start_col] == '.':
                continue
            if query_sum(row + 1, start_col + 1, row + 1, x2) == 0:
                return True
        return False

    # Check if the subgrid is determinable
    for col in range(x1 - 1, x2):
        if not is_exitable(col):
            return False
    return True

