    def is_portal(top_left_row: int, top_left_col: int, bottom_right_row: int, bottom_right_col: int) -> bool:
        for row in range(top_left_row + 1, bottom_right_row):
            if grid[row][top_left_col] != 1 or grid[row][bottom_right_col] != 1:
                return False
        for col in range(top_left_col + 1, bottom_right_col):
            if grid[top_left_row][col] != 1 or grid[bottom_right_row][col] != 1:
                return False
        return True

    def calculate_operations(top_left_row: int, top_left_col: int, bottom_right_row: int, bottom_right_col: int) -> int:
        operations = 0
        for row in range(top_left_row + 1, bottom_right_row):
            if grid[row][top_left_col] == 1:
                operations += 1
            if grid[row][bottom_right_col] == 1:
                operations += 1
        for col in range(top_left_col + 1, bottom_right_col):
            if grid[top_left_row][col] == 1:
                operations += 1
            if grid[bottom_right_row][col] == 1:
                operations += 1
        return operations

    min_operations = float('inf')

    # Iterate over all possible top-left and bottom-right corners of the rectangle
    for top_left_row in range(n - 4):
        for top_left_col in range(m - 3):
            for bottom_right_row in range(top_left_row + 4, n):
                for bottom_right_col in range(top_left_col + 3, m):
                    if is_portal(top_left_row, top_left_col, bottom_right_row, bottom_right_col):
                        operations = calculate_operations(top_left_row, top_left_col, bottom_right_row, bottom_right_col)
                        min_operations = min(min_operations, operations)

