def find_desert_pairs(N: int, M: int, edges: List[Tuple[int, int]]) -> int:
    # Initialize the graph and other necessary data structures
    graph = {i: [] for i in range(1, N + 1)}
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    # Function to find the connected components in the graph
    def find_connected_components(graph):
        visited = set()
        components = []

        def dfs(node):
            stack = [node]
            component = []
            while stack:
                current = stack.pop()
                if current not in visited:
                    visited.add(current)
                    component.append(current)
                    for neighbor in graph[current]:
                        if neighbor not in visited:
                            stack.append(neighbor)
            return component

        for node in graph:
            if node not in visited:
                components.append(dfs(node))
        return components

    # Function to check if a graph is a cactus
    def is_cactus(graph, edges, component_edges):
        edge_index = {edge: i for i, edge in enumerate(edges)}
        edge_count = [0] * len(edges)
        for component in component_edges:
            for edge in component:
                edge_count[edge_index[edge]] += 1
                if edge_count[edge_index[edge]] > 1:
                    return False
        return True

    # Find all connected components
    connected_components = find_connected_components(graph)

    # Count the number of desert pairs
    count = 0
    for L in range(M):
        for R in range(L, M):
            # Create a subgraph with edges from L to R
            subgraph_edges = edges[L:R+1]
            subgraph_graph = {i: [] for i in range(1, N + 1)}
            for u, v in subgraph_edges:
                subgraph_graph[u].append(v)
                subgraph_graph[v].append(u)

            # Check if the subgraph is a desert
            subgraph_components = find_connected_components(subgraph_graph)
            if all(is_cactus(subgraph_graph, subgraph_edges, component) for component in subgraph_components):
                count += 1

    return count

