def find_life_permutation(n: int, max_queries: int) -> list:
    permutation = list(range(1, n + 1))
    query_count = 0

    def query(a: list[int]) -> int:
        nonlocal query_count
        if query_count >= max_queries:
            raise ValueError("Max queries exceeded")
        query_count += 1
        s = [p + a[i] for i, p in enumerate(permutation)]
        freq = {}
        for num in s:
            if num in freq:
                return s.index(num) + 1  # Return the index of the duplicate sum
            else:
                freq[num] = 1
        return 0

    # Main logic to determine the permutation
    for i in range(n):
        a = [0] * n
        a[i] = 1
        result = query(a)
        if result != 0:
            permutation[i] = result

    return permutation

