def process_queries(n: int, m: int, q: int, queries: List[Tuple[int, int]]) -> List[int]:
    # Initialize the matrix with all cells free
    matrix = [[True] * m for _ in range(n)]
    results = []
    staircases = set()

    def is_staircase(x1, y1, x2, y2, x3, y3, x4, y4):
        if (0 <= x1 < n and 0 <= y1 < m and 0 <= x2 < n and 0 <= y2 < m and 
            0 <= x3 < n and 0 <= y3 < m and 0 <= x4 < n and 0 <= y4 < m):
            if (matrix[x1][y1] and matrix[x2][y2] and matrix[x3][y3] and matrix[x4][y4]):
                return (x1, y1, x2, y2, x3, y3, x4, y4)
        return None

    def add_staircase(x1, y1, x2, y2, x3, y3, x4, y4):
        staircase = is_staircase(x1, y1, x2, y2, x3, y3, x4, y4)
        if staircase:
            staircases.add(staircase)

    def remove_staircase(x1, y1, x2, y2, x3, y3, x4, y4):
        staircase = is_staircase(x1, y1, x2, y2, x3, y3, x4, y4)
        if staircase:
            staircases.remove(staircase)

    def count_staircases():
        return len(staircases)

    # Precompute staircases
    for i in range(n):
        for j in range(m):
            if matrix[i][j]:
                add_staircase(i, j, i + 1, j + 1, i + 2, j + 2, i + 3, j + 3)
                add_staircase(i, j, i + 1, j - 1, i + 2, j - 2, i + 3, j - 3)

    current_count = count_staircases()
    results.append(current_count)

    # Process queries
    for x, y in queries:
        # Flip the state of the cell
        matrix[x - 1][y - 1] = not matrix[x - 1][y - 1]
        if matrix[x - 1][y - 1]:
            for i in range(max(0, x - 3), min(n, x + 1)):
                for j in range(max(0, y - 3), min(m, y + 1)):
                    add_staircase(i, j, i + 1, j + 1, i + 2, j + 2, i + 3, j + 3)
                    add_staircase(i, j, i + 1, j - 1, i + 2, j - 2, i + 3, j - 3)
        else:
            for i in range(max(0, x - 3), min(n, x + 1)):
                for j in range(max(0, y - 3), min(m, y + 1)):
