def count_good_colorings(k: int, n: int, colored_nodes: List[Tuple[int, str]]) -> int:
    # Define the valid neighboring colors for each color
    valid_neighbors = {
        'white': {'yellow'},
        'yellow': {'white'},
        'green': {'blue'},
        'blue': {'green'},
        'red': {'orange'},
        'orange': {'red'}
    }

    # Create a set of all possible colors
    all_colors = {'white', 'yellow', 'green', 'blue', 'red', 'orange'}

    # Create a dictionary to store the color of each node
    color_map = {i: "" for i in range(1, 2**k + 1)}

    # Assign the given colors to the nodes
    for v, color in colored_nodes:
        color_map[v] = color

    # Function to count the number of good colorings
    def count_ways(node: int) -> int:
        if node >= 2**k:
            return 1

        # Get the current node's color
        current_color = color_map[node]

        # If the node is already colored, count the ways for its children
        if current_color is not None:
            return count_ways(2 * node) * count_ways(2 * node + 1)
        else:
            ways = 0
            for color in all_colors:
                if all(valid_neighbors[color].isdisjoint(valid_neighbors[color_map[neighbor]]) for neighbor in (2 * node, 2 * node + 1) if neighbor in color_map):
                    color_map[node] = color
                    ways = (ways + count_ways(2 * node) * count_ways(2 * node + 1)) % MOD
            color_map[node] = None
            return ways

    # Start counting from the root node
    return count_ways(1)

