def calculate_maintenance(n: int, m: int, train_data: List[Tuple[int, int]], operations: List[Tuple[int, int]]) -> List[int]:
    # Initialize the maintenance schedule for each train
    maintenance_schedule = {i: [] for i in range(1, n + 1)}

    # Process the operations
    for op, k in operations:
        if op == 1:
            x_i, y_i = train_data[k - 1]
            # Add the train's maintenance schedule
            for day in range(1, m + 1):
                if day % (x_i + y_i) <= x_i:
                    maintenance_schedule[k].append(day)
        elif op == 2:
            x_i, y_i = train_data[k - 1]
            # Remove the train's maintenance schedule
            maintenance_schedule[k] = [day for day in maintenance_schedule[k] if day + x_i + y_i > m]

    # Calculate the number of trains in maintenance each day
    result = [0] * m
    for day in range(1, m + 1):
        for train in maintenance_schedule.values():
            if train and train[0] <= day:
                result[day - 1] += 1

    return result

